const ErrorMessage = ({ children }) => {
  return (
    <div
      style={{
        width: "100%",
        padding: "10",
        marginbottom: 10,
        borderRadius: 4,
        BackgroundColor: "oranged",
        textAllign: "center",
        color: "white",
        textTransform: "capitalize",
      }}
    >
      {Children}
    </div>
  );
};

export default ErrorMessage;
